mapper.select:

SELECT ${fieldSql} FROM
${tableName} as f,
(
select  substr(
LISTAGG2(id,', ') WITHIN GROUP(${orderBySql})
,1,instr( LISTAGG2(id,', ') WITHIN GROUP( ${orderBySql}),',',1,1)-1) as tid   from ${tableName}
where EXISTS (  SELECT 1 from ums_user_info_view a, ums_court_full b where a.court_no = b.court_no ${info}
and ${tableName}.user_id = a.id)
group by user_id
)as t where f.id = t.tid

SELECT * from ums_level_info_view c
where c.is_yefg = 1 and  EXISTS ( SELECT 1 from ums_user_info_view a, ums_court_full b where a.court_no = b.court_no ${info}
and c.user_id = a.id)

SELECT f.n_job_report_text,f.user_id, DATE_FORMAT(d_assign_date, 'YYYY-MM-DD') AS d_assign_date from ums_administrative_job_view f, ( SELECT substr( LISTAGG2(id,', ') WITHIN GROUP(ORDER BY d_assign_date DESC) ,1,instr( LISTAGG2(id,', ') WITHIN GROUP(ORDER BY d_assign_date DESC),',',1,1)-1) as tid from ums_administrative_job_view a, ( SELECT MIN(yefg_start_time) as st, user_id from ums_level_info_view where is_yefg = 1 and EXISTS ( SELECT 1 FROM ums_user_info WHERE ( user_type = 1 AND is_valid = 1 AND leave_reason IS NULL ) AND ums_level_info_view.user_id = ums_user_info.id ) GROUP BY ums_level_info_view.user_id
) b
where a.user_id = b.user_id and a.d_assign_date < st GROUP BY a.user_id
) g where f.id = g.tid
