mapper.select:
SELECT * from (
SELECT gender,nation,political, (SELECT DATE_FORMAT(NOW(), '%Y') - DATE_FORMAT(birthday, '%Y') - (DATE_FORMAT(NOW(), '00-%m-%d') < DATE_FORMAT(birthday, '00-%m-%d'))) AS user_no, education_background,pro_cert,law_position,administration_position,personnel_classification,dept_org_code, (select court_gradation from ums_court_full WHERE court_no=a.court_no) as level FROM ums_user_info a WHERE is_valid=1 and user_type=1
AND a.court_code = #{courtCode}
AND a.preparation = 1
AND(a.preparation in(2,4,5,8) or a.preparation is null)
AND a.preparation = 3
) b where b.level = #{courtLevel}

mapper.update:
MERGE INTO ums_user_info
USING xtpt_t_user
ON (ums_user_info.id = xtpt_t_user.uuid)
WHEN MATCHED THEN
UPDATE SET ums_user_info.dept_org_code=#{newDept}
WHERE ums_user_info.court_no = #{court_no}
AND ums_user_info.department = #{dept_no}

MERGE INTO xtpt_t_user
USING ums_user_info
ON (ums_user_info.id = xtpt_t_user.uuid)
WHEN MATCHED THEN
UPDATE SET xtpt_t_user.dept_org_code=#{newDept}
WHERE ums_user_info.court_no = #{court_no}
AND ums_user_info.department = #{dept_no}

mapper.select:
select ifnull(${leftColumn},'') as leftId,ifnull(${topColumn},'') as topId,count(*) as coutNum from
ums_user_info a,ums_court_full b where a.court_code = b.court_code

    and b.court_gradation = #{courtLevel}


    and b.court_code in

        #{item}



    and user_type = #{userType}


    and user_type = 1
    and IFNULL(leave_reason ,'') = ''


    and preparation = #{preparation}


    ${otherParam}

and a.is_valid = 1
group by ${leftColumn},${topColumn}

SELECT ifnull(${leftColumn},'') as leftId,ifnull(${topColumn},'') as topId,count(*) as coutNum FROM ( select a.*,b.court_std_name,b.court_short_name,c.enter_src,c.company_info_id from ums_user_info a LEFT JOIN ums_court_full b on a.court_code = b.court_code LEFT JOIN ums_external_user_info c on a.id = c.user_id
and b.court_gradation = #{courtLevel}
and b.court_code in
#{item}
and a.user_type = 2 and a.is_valid = 1
) e group by ${leftColumn},${topColumn}

SELECT ifnull(${leftColumn},'') as leftId,ifnull(${topColumn},'') as topId,count(*) as coutNum FROM ( select a.*,b.court_std_name,b.court_short_name,c.juror_work,c.member_state from ums_user_info a LEFT JOIN ums_court_full b on a.court_code = b.court_code LEFT JOIN ums_juror_info c on a.id = c.user_id
and b.court_gradation = #{courtLevel}
and b.court_code in
#{item}
and a.user_type = 3 and a.is_valid = 1
) e group by ${leftColumn},${topColumn}

SELECT


  WHEN age >= #{item.start} AND age <= #{item.end} THEN #{item.name}


IFNULL(${topColumn},'') as topId,
count(*) as coutNum
FROM
(
SELECT

    YEAR (now()) - YEAR (c.birthday) - IF( DATE_FORMAT(NOW(), '%m%d') < DATE_FORMAT(c.birthday, '%m%d'), 1, 0 ) AS age,

c.*
FROM
ums_user_info c,ums_court_full b where c.court_code = b.court_code

    and b.court_gradation = #{courtLevel}


    and b.court_code in

        #{item}



    and user_type = #{userType}


    and user_type = 1
    and IFNULL(leave_reason ,'') = ''


    and preparation = #{preparation}


    ${otherParam}

and c.is_valid = 1
) a
GROUP BY
age,
${topColumn}

SELECT
WHEN age >= #{item.start} AND age <= #{item.end} THEN #{item.name}
IFNULL(${topColumn},'') as topId, count(*) as coutNum FROM ( SELECT
YEAR (now()) - YEAR (c.birthday) - IF( DATE_FORMAT(NOW(), '%m%d') < DATE_FORMAT(c.birthday, '%m%d'), 1, 0 ) AS age,
c.*,b.court_std_name,b.court_short_name,e.enter_src,e.company_info_id from ums_user_info c LEFT JOIN ums_court_full b on c.court_code = b.court_code LEFT JOIN ums_external_user_info e on e.user_id = c.id
and b.court_gradation = #{courtLevel}
and b.court_code in
#{item}
and c.user_type = 2 and c.is_valid = 1
) a GROUP BY age, ${topColumn}

SELECT
WHEN age >= #{item.start} AND age <= #{item.end} THEN #{item.name}
IFNULL(${topColumn},'') as topId, count(*) as coutNum FROM ( SELECT
YEAR (now()) - YEAR (c.birthday) - IF( DATE_FORMAT(NOW(), '%m%d') < DATE_FORMAT(c.birthday, '%m%d'), 1, 0 ) AS age,
c.*,b.court_std_name,b.court_short_name,e.juror_work,e.member_state from ums_user_info c LEFT JOIN ums_court_full b on c.court_code = b.court_code LEFT JOIN ums_juror_info e on e.user_id = c.id
and b.court_gradation = #{courtLevel}
and b.court_code in
#{item}
and c.user_type = 3 and c.is_valid = 1
) a GROUP BY age, ${topColumn}

mapper.update:

update system_platform.ums_user_info a
set a.sort_no=a.sort_no + 1
where a.user_type = #{userType}
  and a.court_no = #{courtNo}
  and a.department = #{deptNo}
  and (a.is_info_complete IS NULL OR a.is_info_complete <> 1)
  and a.is_valid = 1
  and a.sort_no >= #{sortNo}
  and a.leave_reason IS NULL


update system_platform.ums_user_info a
set a.sort_no=a.sort_no + 1
where a.user_type = #{userType}
  and a.court_no = #{courtNo}
  and a.department = #{deptNo}
  and (a.is_info_complete IS NULL OR a.is_info_complete <> 1)
  and a.is_valid = 1
  and a.sort_no > #{sortNo}
  and a.leave_reason IS NULL

